import { navigate, Link } from "@reach/router";
import axios from "axios";
import { useEffect, useState } from "react";

const PetDisplay = props => {
    const [info, setInfo] = useState("");

    useEffect(() => {
        axios.get(`http://localhost:8000/api/pets/${props.id}`)
            .then(res => {
                console.log(res.data.pet)
                setInfo(res.data.pet[0])
            })
            .catch(err => {
                console.log("Error:", err);
            })
    }, []);

    const editHandler = e => {
        navigate(`/${info._id}/edit`);
    }
    const deletePet = e => {
        axios.delete(`http://localhost:8000/api/pets/${info._id}`);
        navigate("/");
    }

    return (
        <div className="body">
        <div className="header">
            <h1>Pet Shelter</h1>
            <Link to="/">back to home</Link>
        </div>
        <div className="header">
            <h2>Details about: {info.name}</h2>
            <button onClick={deletePet}>Adopt {info.name}</button>
        </div>
        <div className="box">
            <div className="row2">
                <div className="column">
                    <p><h3>Pet Type:</h3></p>
                    <p><h3>Description:</h3></p>
                    <p><h3>Skills:</h3></p>
                </div>
                <div className="column">
                    <p>{info.type}</p>
                    <p>{info.description}</p>
                    <p>
                        <ul>
                            <li>{info.skillOne}</li>
                            <li>{info.skillTwo}</li>
                            <li>{info.skillThree}</li>
                        </ul>
                    </p>
                </div>
            </div>
            <p>Type: {info.type}</p>
            <p>Description: {info.description}</p>
            <p>Skill One: {info.skillOne}</p>
            <p>Skill Two: {info.skillTwo}</p>
            <p>Skill Three: {info.skillThree}</p>
            <p onClick={editHandler}>Edit</p>
        </div>
        </div>
    )
}
export default PetDisplay;
