import { navigate } from "@reach/router";
import axios from "axios";
import { useEffect, useState } from "react";

const PetDisplay = props => {
    const [info, setInfo] = useState("");

    useEffect(() => {
        axios.get(`http://localhost:8000/api/pets/${props.id}`)
            .then(res => {
                console.log(res.data.pet)
                setInfo(res.data.pet[0])
            })
            .catch(err => {
                console.log("Error:", err);
            })
    }, []);

    const editHandler = e => {
        navigate(`/${info._id}/edit`);
    }
    const deletePet = e => {
        axios.delete(`http://localhost:8000/api/pets/${info._id}`)
            .then(navigate("/"))
    }

    return (
        <>
            <h1>{info.name}</h1>
            <p>Type: {info.type}</p>
            <p>Description: {info.description}</p>
            <p>Skill One: {info.skillOne}</p>
            <p>Skill Two: {info.skillTwo}</p>
            <p>Skill Three: {info.skillThree}</p>
            <p onClick={editHandler}>Edit</p>
            <button onClick={deletePet}>Delete</button>
        </>
    )
}
export default PetDisplay;
