import React, { useEffect, useState } from 'react'
import axios from 'axios';
export default props => {
    const { id } = props;
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [skillOne, setSkillOne] = useState("");
    const [skillTwo, setSkillTwo] = useState("");
    const [skillThree, setSkillThree] = useState("");
    useEffect(() => {
        axios.get(`http://localhost:8000/api/pets/${props.id}`)
            .then(res => {
                setName(res.data.pet.name);
                setType(res.data.pet.type);
                setDescription(res.data.pet.description);
                setSkillOne(res.data.pet.skillOne);
                setSkillTwo(res.data.pet.skillTwo);
                setSkillThree(res.data.pet.skillThree);
            })
    }, [])
    const updateProduct = e => {
        e.preventDefault();
        axios.put('http://localhost:8000/api/pets/' + id, {
            name,
            type,
            description,
            skillOne,
            skillTwo,
            skillThree
        })
            .then(res => console.log(res));
    }
    return (
        <div>
            <h1>Update a Pet</h1>
            <form onSubmit={updateProduct}>
                <p>
                    <label>Title</label><br />
                    <input type="text" 
                    name="name" 
                    value={name} 
                    onChange={(e) => { setName(e.target.value) }} />
                </p>
                <p>
                    <label>Price</label><br />
                    <input type="text"
                    name="type"
                    value={type} 
                    onChange={(e) => { setType(e.target.value) }} />
                </p>
                <p>
                    <label>Description</label><br />
                    <input type="text"
                    name="description"
                    value={description} 
                    onChange={(e) => { setDescription(e.target.value) }} />
                </p>
                <p>
                    <label>Skill One</label><br />
                    <input type="text"
                    name="skillOne"
                    value={skillOne} 
                    onChange={(e) => { setSkillOne(e.target.value) }} />
                </p>
                <p>
                    <label>Skill Two</label><br />
                    <input type="text"
                    name="skillTwo"
                    value={skillTwo} 
                    onChange={(e) => { setSkillTwo(e.target.value) }} />
                </p>
                <p>
                    <label>Skill Three</label><br />
                    <input type="text"
                    name="skillThree"
                    value={skillThree} 
                    onChange={(e) => { setSkillThree(e.target.value) }} />
                </p>
                <input type="submit" value="Update Pet" />
            </form>
        </div>
    )
}
